import React from 'react';
import { addFavoriteMovie } from './api';

const AddFavouriteMovies = ({ movieId }) => {
    const handleAddFavorite = async () => {
        const { success, data } = await addFavoriteMovie(movieId);
        if (success) {
            alert('Фильм добавлен в избранное');
        } else {
            alert(`Ошибка: ${data}`);
        }
    };

    return (
        <button onClick={handleAddFavorite} className="btn btn-primary">
            Add to Favorites
        </button>
    );
};

export default AddFavouriteMovies;
