import React, { useEffect, useState } from 'react';
import { getFavorites, removeFromFavorites } from './api';

function MyFavoriteMovies() {
    const [favorites, setFavorites] = useState([]);

    useEffect(() => {
        const loadFavorites = async () => {
            const { success, data } = await getFavorites();
            if (success) {
                setFavorites(data);
            }
        }
        loadFavorites();
    }, []);

    const handleRemoveFromFavorites = async (movieId) => {
        const { success, data } = await removeFromFavorites(movieId);
        if (success) {
            alert(data.message);
            setFavorites(favorites.filter(movie => movie.id !== movieId));
        } else {
            alert(data);
        }
    }

    return (
        <div className="container bg-light mb-5 pb-5 pt-3">
            <div className="row">
                {
                    favorites.map((movie) => {
                        return (
                            <div className="col-sm-6" key={movie.id}>
                                <div className="card mt-4 py-3 mx-5">
                                    <div className="card-body">
                                        <h5 className="card-title">{movie.title}</h5>
                                        <button onClick={() => handleRemoveFromFavorites(movie.id)} className="btn btn-danger">Remove from Favorites</button>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
}

export default MyFavoriteMovies;
