import React, { useEffect, useState } from 'react';
import { getMyFavoriteMovies, deleteFavoriteMovie } from './api';
import { useNavigate } from 'react-router-dom';

export default function MyFavoriteMovies() {
    const navigate = useNavigate();
    const [favorites, setFavorites] = useState([]);

    useEffect(() => {
        const loadFavorites = async () => {
            const { success, data } = await getMyFavoriteMovies();
            if (success) {
                setFavorites(data);
            } else {
                navigate("/login", { state: { message: data } });
            }
        };
        loadFavorites();
    }, [navigate]);

    const handleDeleteFavorite = async (movieId) => {
        const response = await deleteFavoriteMovie(movieId);
        if (response.success) {
            setFavorites(favorites.filter(movie => movie.id !== movieId));
        } else {
            alert("Failed to delete movie from favorites.");
        }
    };

    return (
        <div className="container mt-5">
            <h2>My Favorite Movies</h2>
            <div className="row">
                {favorites.map(movie => (
                    <div className="col-sm-6" key={movie.id}>
                        <div className="card mt-4 py-3 mx-5">
                            <div className="card-body">
                                <h5 className="card-title">{movie.title}</h5>
                                <p className="card-text">Price: {movie.price}</p>
                                <button onClick={() => handleDeleteFavorite(movie.id)} className="btn btn-danger">Remove from Favorites</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
