import { Link, useNavigate, useParams, useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import { getOneMovie, addFavoriteMovie, removeFavoriteMovie, getFavoriteMovies } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function MovieItem() {
    const location = useLocation();
    const navigate = useNavigate();
    const params = useParams();
    const [movie, setMovie] = useState([]);
    const [hasToken, setHasToken] = useState(false);
    const [isFavorite, setIsFavorite] = useState(false);

    useEffect(() => {
        if (location.state) {
            toast(location.state.message);
            location.state = null;
        }

        const user = JSON.parse(localStorage.getItem("user"));
        if (user)
            setHasToken(true);
    }, [location]);

    useEffect(() => {
        const loadMovie = async () => {
            const { success, data } = await getOneMovie(params.movieId);
            if (success) {
                setMovie(data);
            } else {
                navigate("/login", { state: { message: data } });
            }
        }
        loadMovie();
    }, [params.movieId]);

    useEffect(() => {
        const checkFavorite = async () => {
            const { success, data } = await getFavoriteMovies();
            if (success) {
                setIsFavorite(data.some(favMovie => favMovie.id === movie.id));
            }
        }
        checkFavorite();
    }, [movie.id]);

    const handleAddFavorite = async () => {
        const { success, data } = await addFavoriteMovie(movie.id);
        if (success) {
            toast(data);
            setIsFavorite(true);
        } else {
            toast(data);
        }
    }

    const handleRemoveFavorite = async () => {
        const { success, data } = await removeFavoriteMovie(movie.id);
        if (success) {
            toast(data);
            setIsFavorite(false);
        } else {
            toast(data);
        }
    }

    return (
        <div className="container">
            <ToastContainer />
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                    <p className="card-text"><br />Описание: {movie.content}<br /><br />Price: {movie.price}<br /> </p>
                    {hasToken && (
                        isFavorite ? (
                            <button onClick={handleRemoveFavorite} className="btn btn-danger">Remove from Favorites</button>
                        ) : (
                            <button onClick={handleAddFavorite} className="btn btn-success">Add to Favorites</button>
                        )
                    )}
                </div>
            </div>
        </div>
    );
}
