// AddFavouriteMovies.js
import { useState } from 'react';
import { addFavoriteMovie } from './api';

export default function AddFavouriteMovies({ movieId }) {
    const [message, setMessage] = useState("");

    const handleAddToFavorites = async () => {
        const response = await addFavoriteMovie(movieId);
        setMessage(response.message);
    };

    return (
        <div>
            <button onClick={handleAddToFavorites} className="btn btn-primary">Add to Favorites</button>
            {message && <p>{message}</p>}
        </div>
    );
}
