import { Link, Outlet, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { getAllMovies } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';
import './css/custom.css';  // Import the custom CSS

export default function MyMovies() {
    const navigate = useNavigate();
    const [movies, setMovies] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const loadMovies = async () => {
            const { success, data } = await getAllMovies();
            if (success) {
                setMovies(data);
            } else {
                navigate("/login", { state: { message: data } });
            }
        }
        loadMovies();
    }, []);

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const filteredMovies = movies.filter(movie =>
        movie.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="container bg-light mb-5 pb-5 pt-3">
            <div className="row">
                <div className="col-12 mb-4">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search for a movie..."
                        value={searchTerm}
                        onChange={handleSearchChange}
                    />
                </div>
                {filteredMovies.map((movie) => (
                    <div className="col-md-6 col-lg-4" key={movie.id}>
                        <div className="card mt-4 py-3 mx-5">
                            <img src={movie.photo ? `src/images/${movie.photo}` : 'default_image_path.jpg'} className="card-img-top" alt={movie.title} />
                            <div className="card-body">
                                <h5 className="card-title">
                                    <Link className="nav-link" to={'/movies/' + movie.id}>{movie.title}</Link>
                                </h5>
                                <p className="card-text">Price: {movie.price}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <div>
                <Outlet />
            </div>
        </div>
    );
}
