import { Link, Outlet, useNavigate } from "react-router-dom";
import { useEffect,useState } from "react";
import { getAllMovies, addToFavorites ,getAllUsers } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function MyMovies(){

    const navigate = useNavigate();
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        const loadMovies = async () => {
            const {success, data } = await getAllMovies();
            if(success){
                setMovies(data);
            }
            else{
                navigate("/login", {state: { message: data }});
            }
        }
        loadMovies();
    },[]);

    const handleAddToFavorites = async (movieId) => {
        const { success, data } = await addToFavorites(movieId);
        if (success) {
            alert(data.message);
        } else {
            alert(data);
        }
    }

    return (
        <div className="container bg-light mb-5 pb-5 pt-3">
            <div className="row">
                {
                    movies.map((movie) => {
                        return (
                            <div className="col-sm-6" key={movie.id}>
                                <div className="card mt-4 py-3 mx-5">
                                    <div className="card-body">
                                        <h5 className="card-title"><Link className="nav-link" to={'/movies/' + movie.id}>{movie.title}</Link></h5>
                                        <p className="card-text">Price: {movie.price}</p>
                                        <button onClick={() => handleAddToFavorites(movie.id)} className="btn btn-primary">Add to Favorites</button>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
            <div>
                <Outlet />
            </div>
        </div>
    )
};
