import { Link, Outlet, useNavigate, useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import { getMyMovies } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function MyMovies() {

    const navigate = useNavigate();
    const [movies, setMovies] = useState([]);

    const { state } = useLocation();
    useEffect(() => {
        if (state && state.fromLogin === true) {
            toast('Succes Login');
        }
    }, [state]);

    useEffect(() => {
        const loadMovies = async () => {
            const { success, data } = await getMyMovies();
            if (success) {
                setMovies(data);
            }
            else {
                navigate("/login", { state: { message: "Пост жоқ!!!" } });
            }
        }
        loadMovies();
    }, [navigate]);

    return (
        <div className="container">
            <div className="row row-cols-1 row-cols-md-3 g-4">
                {
                    movies.map((movie) => {
                        return (
                            <div className="col" key={movie.id}>
                                <div className="card mt-4 py-3">
                                    <div className="card-body">
                                        <h5 className="card-title"><Link className="nav-link" to={'/movies/' + movie.id}>{movie.title}</Link></h5>
                                        <Link to={'/movies/' + movie.id + '/edit'} className="btn btn-primary">Edit</Link> &nbsp;
                                        <Link to={'/movies/' + movie.id + '/delete'} className="btn btn-danger">Delete</Link>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
            <Link to={'/movies/create'} className="btn btn-warning mx-5 mt-4">Фильм қосу</Link>
            <div>
                <Outlet />
            </div>
        </div>
    )
};
