import { useState, useEffect } from "react";
import { getOneUser, editUser } from './api';
import { useParams, useNavigate } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function EditUser() {
    const navigate = useNavigate();
    const { userId } = useParams();
    const [user, setUser] = useState({
        email: '',
        password: '',
        username: '',
        role: 'user'
    });

    useEffect(() => {
        const loadUser = async () => {
            const { success, data } = await getOneUser(userId);
            if (success) {
                setUser(data);
            } else {
                toast.error("Error loading user");
            }
        };
        loadUser();
    }, [userId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser((prevUser) => ({
            ...prevUser,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const success = await editUser(user);
        if (success) {
            toast.success("User edited successfully");
            navigate('/admin/users');
        } else {
            toast.error("Error editing user");
        }
    };

    return (
        <div className="container">
            <h2>Edit User</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email:</label>
                    <input type="email" id="email" name="email" className="form-control" value={user.email} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password:</label>
                    <input type="password" id="password" name="password" className="form-control" value={user.password} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label htmlFor="username" className="form-label">Username:</label>
                    <input type="text" id="username" name="username" className="form-control" value={user.username} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label htmlFor="role" className="form-label">Role:</label>
                    <select id="role" name="role" className="form-control" value={user.role} onChange={handleChange}>
                        <option value="user">User</option>
                        <option value="admin">Admin</option>
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Edit User</button>
            </form>
            <ToastContainer />
        </div>
    );
}

export default EditUser;
