import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getFavoriteMovies, removeFavoriteMovie } from './api';

function Profile() {
    const [user, setUser] = useState(null);
    const [favorites, setFavorites] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (storedUser) {
            setUser(storedUser);
            loadFavoriteMovies();
        } else {
            navigate('/login');
        }
    }, [navigate]);

    const loadFavoriteMovies = async () => {
        const { success, data } = await getFavoriteMovies();
        if (success) {
            setFavorites(data);
        }
    };

    const handleRemoveFavorite = async (movieId) => {
        const { success, data } = await removeFavoriteMovie(movieId);
        if (success) {
            alert('Фильм удален из избранного');
            loadFavoriteMovies();
        } else {
            alert(`Ошибка: ${data}`);
        }
    };

    const logout = () => {
        localStorage.removeItem("user");
        navigate("/login");
        window.location.reload();
    };

    return (
        <div className="container mt-5">
            {user && (
                <div className="card">
                    <div className="card-header">
                        <h2 className="card-title">Welcome, {user.username}</h2>
                    </div>
                    <div className="card-body">
                        <p className="card-text"><b>Email:</b> {user.email}</p>
                        <p className="card-text"><b>Role:</b> {user.role}</p>
                        <div className="mt-4">
                            <h3>My Favorite Movies</h3>
                            {favorites.length > 0 ? (
                                <ul>
                                    {favorites.map((movie) => (
                                        <li key={movie.id}>
                                            {movie.title}{" "}
                                            <button onClick={() => handleRemoveFavorite(movie.id)} className="btn btn-danger btn-sm">
                                                Delete
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p>You have no favorite movies.</p>
                            )}
                        </div>
                        <div className="mt-4">
                            <button onClick={logout} className="btn btn-danger"><b>Logout</b></button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Profile;
