import React from 'react';
import { addFavoriteMovie } from './api';

export default function AddFavoriteMovies({ movieId }) {
    const handleAddFavorite = async () => {
        const response = await addFavoriteMovie(movieId);
        if (response.success) {
            alert("Movie added to favorites!");
        } else {
            alert("Failed to add movie to favorites.");
        }
    };

    return (
        <button onClick={handleAddFavorite} className="btn btn-primary">Add to Favorites</button>
    );
}
