import React, { useEffect, useState } from 'react';
import { getMyFavoriteMovies, removeFromFavorites } from './api';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function MyFavoriteMovies() {
    const [favoriteMovies, setFavoriteMovies] = useState([]);

    useEffect(() => {
        const loadFavoriteMovies = async () => {
            const { success, data } = await getMyFavoriteMovies();
            console.log('Favorite Movies Response:', data); // Add console log here for debugging
            if (success) {
                setFavoriteMovies(data);
            } else {
                toast.error(data);
            }
        };
        loadFavoriteMovies();
    }, []);

    const handleRemoveFromFavorites = async (movieId) => {
        const { success, data } = await removeFromFavorites(movieId);
        if (success) {
            setFavoriteMovies(favoriteMovies.filter((movie) => movie.id !== movieId));
            toast.success('Movie removed from favorites!');
        } else {
            toast.error(data);
        }
    };

    return (
        <div className="container bg-light mb-5 pb-5 pt-3">
            <ToastContainer />
            <h3>My Favorite Movies</h3>
            <div className="row">
                {favoriteMovies.length > 0 ? (
                    favoriteMovies.map((movie) => (
                        <div className="col-sm-6" key={movie.id}>
                            <div className="card mt-4 py-3 mx-5">
                                <div className="card-body">
                                    <h5 className="card-title">{movie.title}</h5>
                                    <p className="card-text">Price: {movie.price}</p>
                                    <button
                                        className="btn btn-danger"
                                        onClick={() => handleRemoveFromFavorites(movie.id)}
                                    >
                                        Remove from Favorites
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No favorite movies found.</p>
                )}
            </div>
        </div>
    );
}
