import { useState, useEffect } from "react";
import { editCategory, getOneCategory } from './api';
import { useNavigate, useParams } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function EditCategory() {
    const navigate = useNavigate();
    const { categoryId } = useParams();
    const [category, setCategory] = useState({ name: '' });

    useEffect(() => {
        const loadCategory = async () => {
            const { success, data } = await getOneCategory(categoryId);
            if (success) {
                setCategory(data);
            } else {
                toast.error("Error loading category");
                navigate("/login");
            }
        };
        loadCategory();
    }, [categoryId, navigate]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCategory((prevCategory) => ({
            ...prevCategory,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const success = await editCategory(category);
        if (success) {
            toast.success('Category updated successfully!');
            navigate('/categories');
        } else {
            toast.error("Error updating category");
        }
    };

    return (
        <div className="container">
            <h2>Edit Category</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name:</label>
                    <input type="text" id="name" name="name" className="form-control" value={category.name} onChange={handleChange} required />
                </div>
                <button type="submit" className="btn btn-primary">Edit Category</button>
            </form>
            <ToastContainer />
        </div>
    );
}

export default EditCategory;
