import { useState, useEffect } from "react";
import { editCategory, getOneCategory } from './api';
import { useNavigate, useParams } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function EditCategory() {
    const navigate = useNavigate();
    const params = useParams();
    const [category, setCategory] = useState({});

    useEffect(() => {
        const loadCategory = async () => {
            try {
                const { success, data } = await getOneCategory(params.categoryId);
                if (success) {
                    setCategory(data);
                } else {
                    toast.error("Error loading category");
                    navigate("/login");
                }
            } catch (error) {
                toast.error("An error occurred while loading the category");
                navigate("/login");
            }
        };
        loadCategory();
    }, [params.categoryId, navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const success = await editCategory(category);
            if (success) {
                toast.success("Category updated successfully");
                navigate('/categories');
            } else {
                toast.error("Error editing category");
            }
        } catch (error) {
            toast.error("An error occurred while editing the category");
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCategory({ ...category, [name]: value });
    };

    return (
        <div className="container">
            <h2>Edit Category</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        className="form-control"
                        value={category.name || ''}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit" className="btn btn-primary">Edit</button>
            </form>
            <ToastContainer />
        </div>
    );
}

export default EditCategory;
