// MyFavouriteMovies.js
import { useEffect, useState } from 'react';
import { getFavoriteMovies, removeFavoriteMovie } from './api';

export default function MyFavouriteMovies() {
    const [favorites, setFavorites] = useState([]);

    useEffect(() => {
        const loadFavorites = async () => {
            const { success, data } = await getFavoriteMovies();
            if (success) {
                setFavorites(data);
            }
        };
        loadFavorites();
    }, []);

    const handleRemove = async (movieId) => {
        const response = await removeFavoriteMovie(movieId);
        if (response.success) {
            setFavorites(favorites.filter(movie => movie.id !== movieId));
        }
    };

    return (
        <div className="container mt-5">
            <h2>My Favorite Movies</h2>
            <div className="row">
                {favorites.map((movie) => (
                    <div className="col-sm-6" key={movie.id}>
                        <div className="card mt-4 py-3 mx-5">
                            <div className="card-body">
                                <h5 className="card-title">{movie.title}</h5>
                                <p className="card-text">Price: {movie.price}</p>
                                <button onClick={() => handleRemove(movie.id)} className="btn btn-danger">Remove from Favorites</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
