import { useEffect, useState } from 'react';
import { getFavoriteMovies, removeMovieFromFavorites } from './api';

function MyFavouriteMovies() {
    const [favorites, setFavorites] = useState([]);

    useEffect(() => {
        const fetchFavorites = async () => {
            const { success, data } = await getFavoriteMovies();
            if (success) {
                setFavorites(data);
            }
        };
        fetchFavorites();
    }, []);

    const handleRemove = async (movieId) => {
        const { success, data } = await removeMovieFromFavorites(movieId);
        if (success) {
            setFavorites(favorites.filter(movie => movie.id !== movieId));
            alert(data.message);
        } else {
            alert(data);
        }
    };

    return (
        <div className="container">
            <h2>My Favourite Movies</h2>
            <div className="row">
                {favorites.map((movie) => (
                    <div className="col-sm-6" key={movie.id}>
                        <div className="card mt-4 py-3 mx-5">
                            <div className="card-body">
                                <h5 className="card-title">{movie.title}</h5>
                                <p className="card-text">Price: {movie.price}</p>
                                <button className="btn btn-danger" onClick={() => handleRemove(movie.id)}>Remove from Favorites</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default MyFavouriteMovies;
