import React, { useEffect, useState } from 'react';
import { getAllMovies, addToFavorites } from './api';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function AddFavoriteMovies() {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        const loadMovies = async () => {
            const { success, data } = await getAllMovies();
            if (success) {
                setMovies(data);
            } else {
                toast.error(data);
            }
        };
        loadMovies();
    }, []);

    const handleAddToFavorites = async (movieId) => {
        const { success, data } = await addToFavorites(movieId);
        if (success) {
            toast.success('Movie added to favorites!');
        } else {
            toast.error(data);
        }
    };

    return (
        <div className="container bg-light mb-5 pb-5 pt-3">
            {/* <ToastContainer /> */}
            <h3>Add Favorite Movies</h3>
            <div className="row">
                {movies.map((movie) => (
                    <div className="col-sm-6" key={movie.id}>
                        <div className="card mt-4 py-3 mx-5">
                            <div className="card-body">
                                <h5 className="card-title">{movie.title}</h5>
                                <p className="card-text">Price: {movie.price}</p>
                                <button
                                    className="btn btn-primary"
                                    onClick={() => handleAddToFavorites(movie.id)}
                                >
                                    Add to Favorites
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
