import { useState } from "react";
import { doRegister } from './api';
import { Link, useNavigate } from "react-router-dom";
import { toast, ToastContainer } from 'react-toastify';
import { Button, Form } from 'react-bootstrap';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function RegisterForm() {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [username, setUsername] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const { success, data } = await doRegister(email, password, username);
        if (success) {
            navigate('/login', { state: { message: data } });
        } else {
            toast(data.response.data.message);
        }
    };

    return (
        <div className="container">
            <ToastContainer />
            <div className="mx-5 px-5">
                <div className="mx-5 px-5">
                    <h2>Register Form</h2>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group className="mb-4" controlId="formUsername">
                            <Form.Control
                                type="text"
                                placeholder="Username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                            />
                        </Form.Group>
                        <Form.Group className="mb-4" controlId="formEmail">
                            <Form.Control
                                type="email"
                                placeholder="Email address"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </Form.Group>
                        <Form.Group className="mb-4" controlId="formPassword">
                            <Form.Control
                                type="password"
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </Form.Group>
                        <Button variant="primary" type="submit" className="btn-block mb-4">
                            Кіру
                        </Button>
                        <div className="text-center">
                            <b>If you have an account - </b>
                            <Link to={'/login'}>
                                <Button variant="outline-secondary" size="sm">
                                    Login
                                </Button>
                            </Link>
                        </div>
                    </Form>
                </div>
            </div>
        </div>
    );
}

export default RegisterForm;
