import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { getFavoriteMovies, removeFavoriteMovie } from './api';

function Profile() {
    const [user, setUser] = useState(null);
    const [favoriteMovies, setFavoriteMovies] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (storedUser) {
            setUser(storedUser);
            loadFavoriteMovies();
        } else {
            navigate('/login');
        }
    }, [navigate]);

    const loadFavoriteMovies = async () => {
        const { success, data } = await getFavoriteMovies();
        if (success) {
            setFavoriteMovies(data);
        } else {
            alert(data);
        }
    };

    const handleRemoveFavorite = async (movieId) => {
        const { success, message } = await removeFavoriteMovie(movieId);
        if (success) {
            setFavoriteMovies(favoriteMovies.filter(movie => movie.id !== movieId));
        } else {
            alert(message);
        }
    };

    const logout = () => {
        localStorage.removeItem("user");
        navigate("/login");
        window.location.reload();
    };

    return (
        <div className="container mt-5">
            {user && (
                <div className="card">
                    <div className="card-header">
                        <h2 className="card-title">Welcome, {user.username}</h2>
                    </div>
                    <div className="card-body">
                        <p className="card-text"><b>Email:</b> {user.email}</p>
                        <p className="card-text"><b>Role:</b> {user.role}</p>
                        {user.role === 'admin' && (
                            <div className="mt-4">
                                <h3>Admin Section</h3>
                                <p>Only admins can see this section.</p>
                                <div className="d-flex gap-2 mt-3">
                                    <Link to="/admin/users" className="btn btn-secondary"><b>Users</b></Link>
                                    <Link to="/admin/movies" className="btn btn-secondary"><b>Movies</b></Link>
                                    <Link to="/admin/categories" className="btn btn-secondary"><b>Categories</b></Link>
                                </div>
                            </div>
                        )}
                        {user.role === 'user' && (
                            <div className="mt-4">
                                <h3>My Favorite Movies</h3>
                                <ul className="list-group">
                                    {favoriteMovies.map((movie) => (
                                        <li key={movie.id} className="list-group-item d-flex justify-content-between align-items-center">
                                            {movie.title}
                                            <button onClick={() => handleRemoveFavorite(movie.id)} className="btn btn-danger btn-sm">
                                                Remove
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        )}
                        <div className="mt-4">
                            <button onClick={logout} className="btn btn-danger"><b>Logout</b></button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Profile;
